+/**
+ * The Generics - Service Worker
+ * Enables offline functionality and caching for the PWA
+ */
+
+const CACHE_NAME = 'the-generics-v1';
+const ASSETS_TO_CACHE = [
+  '/',
+  '/index.html',
+  '/about.html',
+  '/store.html',
+  '/css/modern-style.css',
+  '/js/modern-script.js',
+  '/js/store.js',
+  '/img/Band Members.png',
+  '/img/favicon.svg',
+  '/img/favicon.png',
+  '/img/Youtube Logo.png',
+  '/img/Spotify Logo.png',
+  '/img/Facebook Logo.png',
+  // Add other important assets here
+];
+
+// Install event - cache assets
+self.addEventListener('install', event => {
+  event.waitUntil(
+    caches.open(CACHE_NAME)
+      .then(cache => {
+        console.log('Caching app assets');
+        return cache.addAll(ASSETS_TO_CACHE);
+      })
+  );
+});
+
+// Activate event - clean up old caches
+self.addEventListener('activate', event => {
+  event.waitUntil(
+    caches.keys().then(cacheNames => {
+      return Promise.all(
+        cacheNames.map(cacheName => {
+          if (cacheName !== CACHE_NAME) {
+            console.log('Removing old cache', cacheName);
+            return caches.delete(cacheName);
+          }
+        })
+      );
+    })
+  );
+  return self.clients.claim();
+});
+
+// Fetch event - serve from cache, fall back to network
+self.addEventListener('fetch', event => {
+  event.respondWith(
+    caches.match(event.request)
+      .then(response => {
+        // Cache hit - return the response from the cached version
+        if (response) {
+          return response;
+        }
+        
+        // Not in cache - return the result from the live server
+        // Clone the request because it's a one-time use stream
+        return fetch(event.request).then(
+          networkResponse => {
+            // Don't cache if not a valid response or not a GET request
+            if (!networkResponse || networkResponse.status !== 200 || event.request.method !== 'GET') {
+              return networkResponse;
+            }
+
+            // Clone the response because it's a one-time use stream
+            const responseToCache = networkResponse.clone();
+
+            caches.open(CACHE_NAME)
+              .then(cache => {
+                cache.put(event.request, responseToCache);
+              });
+
+            return networkResponse;
+          }
+        );
+      })
+      .catch(error => {
+        // If both cache and network fail, show an offline page
+        console.log('Fetch failed; returning offline page', error);
+        
+        // You can return a custom offline page here
+        // return caches.match('/offline.html');
+      })
+  );
+});
+
+// Push notification event
+self.addEventListener('push', event => {
+  const title = 'The Generics';
+  const options = {
+    body: event.data.text() || 'Novidades da banda!',
+    icon: '/img/favicon.png',
+    badge: '/img/favicon.png'
+  };
+
+  event.waitUntil(
+    self.registration.showNotification(title, options)
+  );
+});
+
+// Notification click event
+self.addEventListener('notificationclick', event => {
+  event.notification.close();
+  
+  event.waitUntil(
+    clients.openWindow('https://thegenerics.com')
+  );
+});

