+  --color-text-secondary: #b0b0b0;
+  --color-primary: #f48fb1;
+  --color-primary-light: #f8bbd0;
+  --color-primary-dark: #e91e63;
+  --color-secondary: #64b5f6;
+  --color-accent: #ffb74d;
+  --color-border: #424242;
+  --color-card-bg: #1e1e1e;
+  --color-header-bg: rgba(18, 18, 18, 0.95);
+  --color-footer-bg: #000000;
+  --color-footer-text: #f5f5f5;
+  --color-shadow: rgba(0, 0, 0, 0.3);
+  --color-overlay: rgba(0, 0, 0, 0.8);
+  --color-success: #81c784;
+  --color-error: #e57373;
+  --color-warning: #ffb74d;
+  --color-info: #64b5f6;
+}
+
+/* High Contrast Mode */
+.high-contrast {
+  --color-background: #000000;
+  --color-text: #ffffff;
+  --color-text-secondary: #ffffff;
+  --color-primary: #ffff00;
+  --color-primary-light: #ffff00;
+  --color-primary-dark: #ffff00;
+  --color-secondary: #00ffff;
+  --color-accent: #ff00ff;
+  --color-border: #ffffff;
+  --color-card-bg: #000000;
+  --color-header-bg: #000000;
+  --color-footer-bg: #000000;
+  --color-footer-text: #ffffff;
+  --color-shadow: rgba(255, 255, 255, 0.3);
+  --color-overlay: rgba(0, 0, 0, 0.9);
+  --color-success: #00ff00;
+  --color-error: #ff0000;
+  --color-warning: #ffff00;
+  --color-info: #00ffff;
+  
+  /* High contrast specific overrides */
+  --focus-outline: 3px solid #ffff00;
+}
+
+/* Apply theme to elements */
+body {
+  background-color: var(--color-background);
+  color: var(--color-text);
+  transition: background-color 0.3s ease, color 0.3s ease;
+}
+
+/* Links */
+a {
+  color: var(--color-primary);
+  transition: color 0.3s ease;
+}
+
+a:hover, a:focus {
+  color: var(--color-primary-dark);
+}
+
+/* Focus styles for accessibility */
+:focus {
+  outline: 2px solid var(--color-primary);
+  outline-offset: 2px;
+}
+
+.high-contrast :focus {
+  outline: var(--focus-outline);
+}
+
+/* Buttons */
+.btn {
+  background-color: var(--color-primary);
+  color: white;
+  transition: background-color 0.3s ease, transform 0.2s ease;
+}
+
+.btn:hover, .btn:focus {
