+  border-bottom: 1px solid var(--color-border);
+}
+
+/* Footer */
+.main-footer {
+  background-color: var(--color-footer-bg);
+  color: var(--color-footer-text);
+}
+
+/* Theme Toggle Button */
+.theme-toggle-container {
