+.lightbox-prev:hover,
+.lightbox-next:hover,
+.lightbox-close:hover,
+.lightbox-prev:focus,
+.lightbox-next:focus,
+.lightbox-close:focus {
+  background: rgba(233, 30, 99, 0.7);
+}
+
+/* Quick View Modal */
+.quick-view-modal {
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background-color: rgba(0, 0, 0, 0.8);
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  z-index: 1000;
+  opacity: 0;
+  visibility: hidden;
+  transition: opacity 0.3s ease, visibility 0.3s ease;
+}
+
+.quick-view-modal.active {
+  opacity: 1;
+  visibility: visible;
+}
+
+.quick-view-content {
+  background-color: var(--color-background);
+  border-radius: 8px;
+  max-width: 900px;
+  width: 90%;
+  display: flex;
+  position: relative;
+  overflow: hidden;
+}
+
+.quick-view-close {
+  position: absolute;
+  top: 10px;
+  right: 10px;
+  background: none;
+  border: none;
+  color: var(--color-text);
+  font-size: 1.5rem;
+  cursor: pointer;
+  z-index: 1;
+}
+
+.quick-view-image {
+  flex: 1;
+  padding: 2rem;
+}
+
+.quick-view-image img {
+  width: 100%;
+  height: auto;
+  object-fit: contain;
+}
+
+.quick-view-details {
+  flex: 1;
+  padding: 2rem;
+  display: flex;
+  flex-direction: column;
+}
+
+.quick-view-title {
+  font-size: 1.5rem;
+  margin-bottom: 0.5rem;
+}
+
+.quick-view-price {
+  font-size: 1.2rem;
+  color: var(--color-primary);
+  margin-bottom: 1rem;
+  font-weight: bold;
+}
+
+.quick-view-description {
+  margin-bottom: 1.5rem;
+  flex-grow: 1;
+}
+
+/* Responsive Styles */
+@media (max-width: 768px) {
+  .gallery-grid {
+    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
+  }
+  
+  .quick-view-content {
+    flex-direction: column;
+    max-height: 90vh;
+    overflow-y: auto;
+  }
+  
+  .quick-view-image,
+  .quick-view-details {
+    padding: 1rem;
+  }
+  
+  .lightbox-prev,
+  .lightbox-next {
+    width: 40px;
+    height: 40px;
+  }
+  
+  .lightbox-prev {
+    left: 10px;
+  }
+  
+  .lightbox-next {
+    right: 10px;
+  }
+}
+
+@media (max-width: 480px) {
+  .gallery-grid {
+    grid-template-columns: 1fr;
+  }
+  
+  .gallery-filter-btn {
+    margin-bottom: 0.5rem;
+  }
+}

